import base64, struct
bmp_data = base64.b64decode('')

def bmp_info(data):
	s = struct.unpack('<ccIIIIIIHH', data[:30])
	if s[0] and s[1]:
		return {
			'width': s[6],
			'height': s[7],
			'color': s[9]
		}
	else:
		print('非bmp格式')


# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')